def func(d):;
  c = d['card'];
  if isCharacter(c):;
    if color.blue in Colors(c) or color.pink in Colors(c):;
      def cond(d):;
        return d['card'] == d['modifier']['card'];
      def act(d):;
        if d['modifierType'] == modifier.power:;
          d['power'] += 2;
          return True;
        else:;
          if color.blue not in d['colors']: d['colors'].append(color.blue);
          if color.pink not in d['colors']: d['colors'].append(color.pink);
          return True;
      def rem_cond(d):;
        if d['event'] == event.leavesPlay:;
          if d['card'] != d['modifier']['card']: return False;
        return True;
      Modifier([modifier.power, modifier.color], cond, act, [event.endOfPhase, event.leavePlay], rem_cond, card=c);
      d['canceled'] = True;
      playCardCleanup(message=', targeting {}'.format(c));
      return True;
registerEvent(event.preClickCard, func, runOnce=True);
